mapper.select:
select image_id, target_type, filepath
from upload_image_bind
where target_id=#{targetId} and target_type=#{targetType}

mapper.insert:
insert into upload_image_bind (image_id, target_type, target_id, filepath)
# 防止插入已存在的数据
select #{imageId,jdbcType=INTEGER}, #{targetType,jdbcType=INTEGER}, #{targetId,jdbcType=INTEGER},
       #{filepath,jdbcType=VARCHAR}
from dual where not exists(select image_id from upload_image_bind where image_id=#{image_id})

mapper.update:
update upload_image_bind
set target_id = #{newTargetId}, target_type = #{newTargetType}
where target_id = #{oldTargetId} and target_type=#{oldTargetType}
